document.addEventListener("DOMContentLoaded",()=>{"use strict";const startp1=document.getElementById("startp1"),startp2=document.getElementById("startp2"),startp3=document.getElementById("startp3"),howfar=document.getElementById("howfar"),hexcol=document.getElementById("hexcol"),texts=document.getElementById("txtstring"),inputs=document.getElementsByTagName("input"),randcheck=document.getElementById("randomchek");function string(){let string="";for(let i=0;i<howfar.value;i++){const randomColor="#000000".replace(/0/g,(function(){return(~~(16*Math.random())).toString(16)})),color=randcheck.checked?randomColor:hexcol.value;string+=`${0===Number(startp1.value)?0:+startp1.value-i}px ${0===Number(startp2.value)?0:+startp2.value+i}px ${0===Number(startp3.value)?0:+startp3.value+i}px ${color}`,i+1<howfar.value&&(string+=", ")}return string}function appe(){texts.innerText="text-shadow:"+string()+";",texts.style.textShadow=`${string()}`}function Arrows(number){this.number=number}Arrows.prototype.up=function up(y){return this.number=y+1},Arrows.prototype.dn=function dn(y){return this.number=y-1};const lg=new Arrows(0);function updateArrows(e){if(e.target.classList.contains("darrow")||e.target.classList.contains("uarrow")){const g=Number(e.target.parentElement.getElementsByTagName("input")[0].value)||0;"uarrow"===e.target.className&&(e.target.parentElement.getElementsByTagName("input")[0].value=lg.up(g)),"darrow"===e.target.className&&(e.target.parentElement.getElementsByTagName("input")[0].value=lg.dn(g))}appe()}document.addEventListener("click",updateArrows);const le=[...inputs];le.forEach(e=>e.addEventListener("input",appe)),texts.addEventListener("click",(function(e){e.preventDefault(),texts.select(),document.execCommand("copy")})),appe()});